mapper.select:
SELECT SEAT_ID
FROM CUSTOMER_RESERVATION
WHERE RESERVE_ID = #{reserveId}

SELECT BALANCE
FROM ACCOUNT
WHERE ACCOUNT_ID = #{accountId}

mapper.update:
UPDATE ACCOUNT
SET BALANCE = BALANCE + #{amount}
WHERE ACCOUNT_ID = #{accountId}

mapper.select:
SELECT COUNT(*) 
FROM CUSTOMER_RESERVATION
WHERE RESERVE_ID = #{reserveId}
AND SEAT_ID = #{seatId}
